+import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
+import axios from "axios";
+
+const initialState = {
+  orderDetails: null,
+  orderId: null,
+  url: null,
+  isLoading: false,
+};
+
+export const createOrder = createAsyncThunk(
+  "/order/createOrder",
+  async (orderDetails) => {
+    const response = await axios.post(
+      "http://localhost:7000/api/shop/order/payment-checkout",
+      orderDetails
+    );
+    return response.data;
+  }
+);
+
+export const capturePayment = createAsyncThunk(
+  "/order/capturePayment",
+  async (orderId) => {
+    const response = await axios.post(
+      "http://localhost:7000/api/shop/order/capture",
+      {
+        orderId, // Updating payment status
+      }
+    );
+    return response.data;
+  }
+);
+
+const OrderSlice = createSlice({
+  name: "OrderSlice",
+  initialState,
+  reducers: {},
+  extraReducers: (builder) => {
+    builder
+      .addCase(createOrder.pending, (state) => {
+        state.isLoading = true;
+      })
+      .addCase(createOrder.fulfilled, (state, action) => {
+        state.isLoading = false;
+        state.orderDetails = action.payload;
+        state.url = action.payload.url;
+        state.orderId = action.payload.orderId;
+        sessionStorage.setItem("currentOrderId", JSON.stringify(action.payload.orderId));
+        sessionStorage.setItem("currentCartId", JSON.stringify(action.payload.cartId)); // Assuming cartId is part of the payload
+
+
+        // Set currentOrderId and currentCartId in session storage
+        // sessionStorage.setItem("currentCartId", JSON.stringify(action.payload.cartId)); // Assuming cartId is part of the payload
+      })
+      .addCase(createOrder.rejected, (state) => {
+        state.isLoading = false;
+        state.orderDetails = [];
+        state.url = null;
+        state.orderId = null;
+      })
+      .addCase(capturePayment.fulfilled, (state, action) => {
+        state.orderDetails = action.payload;
+      });
+  },
+});
+
+export default OrderSlice.reducer;

