+    e.preventDefault();
+    const droppedFile = e.dataTransfer.files?.[0]
+    if(droppedFile ) setImageFile(droppedFile)
+  }
+
+  function handleRemoveImage(){
+    setImageFile(null);
+    if(inputRef.current){
+        inputRef.current.value = ""
+    }
+  }
+
+  async function uploadImageToCloudinary(){
+    const data = new FormData();
+    data.append('my_file',imageFile)
+    const response = await axios.post('http://localhost:7000/api/admin/products/upload-image',data)
+    console.log(response.data)
+    if(response?.data?.success) setUploadedImageURL(response.data.result.url)
+  }
+
+useEffect(()=>{
+  if(imageFile !== null ) uploadImageToCloudinary()
+},[imageFile])
+
+  return (
+    <div className="w-full max-w-md mx-auto mt-4">
+      <Label className="text-lg font-semibold mb-2 block">Upload Image</Label>
+      <div className="border-2 border-dashed rounded-lg p-4" onDragOver={handleDragOver} onDrop={handleDrop}>
+        <Input
