const express = require("express");
const Stripe = require("stripe")("sk_test_..."); // Your Stripe Secret Key
const Order = require("../../models/Order"); // Import Order model

const router = express.Router();
const endpointSecret = "your_webhook_secret"; // Set in Stripe Dashboard

// Webhook route
router.post("/stripe-webhook", express.raw({ type: "application/json" }), async (req, res) => {
  const sig = req.headers["stripe-signature"];
  let event;

  try {
    event = Stripe.webhooks.constructEvent(req.body, sig, endpointSecret);
  } catch (err) {
    console.error("Webhook signature verification failed:", err.message);
    return res.status(400).send(`Webhook Error: ${err.message}`);
  }

  // Handle successful payments
  if (event.type === "checkout.session.completed") {
    const session = event.data.object;
    const paymentId = session.payment_intent; // Payment ID
    const payerId = session.customer; // Payer ID (Stripe Customer ID)
    const userId = session.metadata.userId; // User ID from metadata

    console.log("Payment Successful!", { paymentId, payerId });

    // Update order in the database
    await Order.findOneAndUpdate(
      { userId: userId },
      { paymentId, payerId, paymentStatus: "paid", orderStatus: "confirmed" },
      { new: true }
    );

    res.json({ received: true });
  } else {
    res.status(400).json({ message: "Unhandled event type" });
  }
});

module.exports = router;
